import debugModule from "debug";

import SolidityUtils from "truffle-solidity-utils";
import CodeUtils from "truffle-code-utils";

const debug = debugModule("debugger:context");

/**
 * Scope
 * @constructor
 */
export default class Context {
  constructor(binary, options) {
    if (binary == null) {
      throw new Error("Can't create context without binary!");
    }

    this.binary = binary;

    if (options.addresses) {
      this.addresses = options.addresses;
    } else if (options.address) {
      this.addresses = new Set([options.address]);
    } else {
      this.addresses = new Set();
    }

    debug("options: %o", options);
    this.sourceMap = options.sourceMap;
    this.source = options.source;
    this.sourcePath = options.sourcePath;
    this.contractName = options.contractName;

    this.instructions = Context.getInstructions(this.binary, this.source, this.sourceMap);

    this.programCounterMapping = Context.getProgramCounterMapping(this.instructions);
    debug("programCounterMapping: %o", this.programCounterMapping);
  }

  merge(other) {
    let binary = this.binary;

    let addresses = new Set([...this.addresses, ...other.addresses]);
    let sourceMap = this.sourceMap || other.sourceMap;
    let source = this.source || other.source;
    let sourcePath = this.sourcePath || other.sourcePath;
    let contractName = this.contractName || other.contractName;

    return new Context(binary, {
      addresses,
      sourceMap,
      source,
      sourcePath,
      contractName
    });
  }

  static getInstructions(binary, source, sourceMap) {
    // raw bytecode instructions
    var instructions = CodeUtils.parseCode(binary);

    var sourceMap = sourceMap;

    if (!sourceMap) {
      debug("no sourcemap");
      sourceMap = "";

      // Let's create a source map to use since none exists. This source map
      // maps just as many ranges as there are instructions, and ensures every
      // instruction is marked as "jumping out". This will ensure all
      // available debugger commands step one instruction at a time.
      //
      // This is kindof a hack; perhaps this should be broken out into separate
      // context types. TODO
      for (var i = 0; i < instructions.length; i++) {
        sourceMap += i + ":" + i + ":1:o;";
      }
    }

    var lineAndColumnMapping = SolidityUtils.getCharacterOffsetToLineAndColumnMapping(source || "");
    var humanReadableSourceMap = SolidityUtils.getHumanReadableSourceMap(sourceMap);

    instructions.forEach(function(instruction, instructionIndex) {
      var sourceMapInstruction = humanReadableSourceMap[instructionIndex];

      instruction.index = instructionIndex;

      if (sourceMapInstruction) {
        instruction.jump = sourceMapInstruction.jump;
        instruction.start = sourceMapInstruction.start;
        instruction.length = sourceMapInstruction.length;
        instruction.range = {
          start: lineAndColumnMapping[sourceMapInstruction.start],
          end: lineAndColumnMapping[sourceMapInstruction.start + sourceMapInstruction.length]
        }
      }
    });

    return instructions;
  }

  /**
   * Returns array, mapping PC index -> Instruction index
   */
  static getProgramCounterMapping(instructions) {
    var map = [];
    instructions.forEach(function(instruction) {
      map[instruction.pc] = instruction.index;
    });

    return map;
  }

  instructionAtProgramCounter(programCounter) {
    debug("programCounter: %O", programCounter);
    debug("instructionIndex: %O", this.programCounterMapping[programCounter]);
    return this.instructions[this.programCounterMapping[programCounter]];
  }
}

import debugModule from "debug";
const debug = debugModule("debugger:selectors:currentContext");

import { createSelector, createStructuredSelector } from "reselect";

import evm from "../evm/selectors";


const contextSet = (state, props) => props.contexts

const currentContext = createSelector(
  [evm.current.call, contextSet],

  ({address, binary}, contexts) => {
    if (address) {
      return contexts.contextForAddress(address);
    } else {
      return contexts.contextForBinary(binary);
    }
  }
)

const affectedInstances = createSelector(
  [contextSet],

  (contexts) => contexts.addressedContracts()
);

const missingSources = createSelector(
  [affectedInstances],

  (instances) => Object.entries(instances)
    .filter(([address, instance]) => !instance.source)
    .map(([address, instance]) => address)
);

let selector = createStructuredSelector({
  current: currentContext,
  affectedInstances: affectedInstances,
  missingSources: missingSources
});
selector.current = currentContext;
selector.affectedInstances = affectedInstances;
selector.missingSources = missingSources;

export default selector;

import Context from "./context";

/**
 * Indexed container collection of Contexts
 * Each Context is indexed by its binary and its associated addresses
 */
export default class ContextSet {
  constructor(...contexts) {
    // initialize empty
    this.contexts = [];
    this.indexForAddress = {};
    this.indexForBinary = {};

    // procedurally add
    this.add(...contexts);
  };

  static forContracts(...contracts) {
    let contexts = [];

    for (let contract of contracts) {
      // create Context for binary and deployed binary
      contexts.push(new Context(contract.binary, {
        sourceMap: contract.sourceMap,
        source: contract.source,
        sourePath: contract.sourcePath,
        contractName: contract.contractName
      }));
      contexts.push(new Context(contract.deployedBinary, {
        sourceMap: contract.deployedSourceMap,
        source: contract.source,
        sourePath: contract.sourcePath,
        contractName: contract.contractName
      }));
    }

    return new ContextSet(...contexts);
  }

  /**
   * Retrieve context for code at a given address
   */
  contextForAddress(address) {
    let index = this.indexForAddress[address];
    return this.contexts[index];
  }

  /**
   * Address -> Contract mapping
   */
  addressedContracts() {
    let map = {};

    for (let address of Object.keys(this.indexForAddress)) {
      let index = this.indexForAddress[address];
      let context = this.contexts[index];

      map[address] = {
        contractName: context.contractName,
        source: context.source,
        binary: context.binary
      };
    }

    return map;
  }


  /**
   * Retrieve context with a given binary
   */
  contextForBinary(binary) {
    let index = this.indexForBinary[binary];
    return this.contexts[index];
  }

  /**
   * Add contexts to the set
   *
   * Merges with existing contexts
   */
  add(...contexts) {
    for (let context of contexts) {
      this.addContext(context);
    }
  };


  /**
   * Add Context
   *
   * If Context already exists in set, merges Context data with existing
   */
  addContext(context) {
    var self = this;
    var index;

    if (this.indexForBinary[context.binary] != undefined) {
      index = this.indexForBinary[context.binary];
      context = context.merge(this.contexts[index]);
    } else {
      index = this.contexts.length;
      this.indexForBinary[context.binary] = index;
    }

    this.contexts[index] = context;

    for (let address of context.addresses) {
      this.indexForAddress[address] = index;
    }
  }

  /**
   * Return a new ContextSet with the merged Contexts of this and another
   */
  merge(other) {
    return new ContextSet([...this.contexts, ...other.contexts]);
  }
}

export const BEGIN_STEP = "BEGIN_STEP";
