import { combineReducers } from "redux";

import * as actions from "./actions";

export function callstack(state = [], action) {
  switch(action.type) {
    case actions.CALL:
      let address = action.address;
      return state + [ {address} ];

    case actions.CREATE:
      const binary = step.createBinary();
      return state + [ {binary} ];

    case actions.RETURN:
      return state.slice(0, -1); // pop

    default:
      return state;
  };
}

const reducer = combineReducers({
  callstack
});

export default reducer;

import debugModule from "debug";
const debug = debugModule("debugger:evm:sagas");

import { call, put, take } from "redux-saga/effects";
import { view } from "../effects";

import { TICK } from "../trace/actions";
import * as actions from "./actions";

import evm from "./selectors";

export function* callstackSaga () {
  while (true) {
    yield take(TICK);
    debug("got TICK");

    if (yield view(evm.nextStep.isCall)) {
      debug("got call");
      let address = yield view(evm.nextStep.callAddress);

      yield put(actions.call(address));

    } else if (yield view(evm.nextStep.isCreate)) {
      debug("got create");
      let binary = yield view(evm.nextStep.createBinary);

      yield put(actions.create(binary));

    } else if (yield view(evm.nextStep.isHalting)) {
      debug("got return");
      yield put(actions.returnCall());
    }
  }
}

export default function* saga () {
  yield call(callstackSaga);
}

import { createSelector, createStructuredSelector } from "reselect";

import trace from "../trace/selectors";

const callstack = (state, props) => state.evm.callstack;

const currentCall = createSelector(
  [callstack],

  (stack) => stack.length ? stack[stack.length - 1] : {}
)

let currentState = createStructuredSelector({
  call: currentCall,
  callstack: callstack
});
currentState.call = currentCall;
currentState.callstack = callstack;


const programCounter = createSelector(
  [trace.step],

  (step) => step.pc
);

const isJump = createSelector(
  [trace.step],

  (step) => step.op != "JUMPDEST" && step.op.indexOf("JUMP") == 0
);

const isCall = createSelector(
  [trace.step],

  (step) => step.op == "CALL" || step.op == "DELEGATECALL"
);

const isCreate = createSelector(
  [trace.step],

  (step) => step.op == "CREATE"
);

const isHalting = createSelector(
  [trace.step],

  (step) => step.op == "STOP" || step.op == "RETURN"
);

const callAddress = createSelector(
  [isCall, trace.step],

  (matches, step) => {
    if (!matches) return null;

    let address = step.stack[step.stack.length - 2]
    address = "0x" + address.substring(24);
    return address;
  }
);

const createBinary = createSelector(
  [isCreate, trace.step],

  (matches, step) => {
    if (!matches) return null;

    const memory = step.memory.join("");

    // Get the code that's going to be created from memory.
    // Note we multiply by 2 because these offsets are in bytes.
    const inputOffset = parseInt(step.stack[step.stack.length - 2], 16) * 2;
    const inputSize = parseInt(step.stack[step.stack.length - 3], 16) * 2;

    return "0x" + memory.substring(inputOffset, inputOffset + inputSize);
  }
);


let nextStep = createStructuredSelector({
  programCounter,

  isJump,
  isCall,
  isCreate,
  isHalting,

  callAddress,
  createBinary
});
nextStep.programCounter = programCounter;
nextStep.isJump = isJump;
nextStep.isCall = isCall;
nextStep.isCreate = isCreate;
nextStep.isHalting = isHalting;
nextStep.callAddress = callAddress;
nextStep.createBinary = createBinary;

let selector = createStructuredSelector({
  current: currentState,
  nextStep
});
selector.current = currentState;
selector.nextStep = nextStep;

export default selector;

