import { all, call, fork, race, take } from 'redux-saga/effects';

import controllerSaga from "./controller/sagas";
import soliditySaga from "./solidity/sagas";
import evmSaga from "./evm/sagas";
import traceSaga from "./trace/sagas";


import { END_OF_TRACE } from "./trace/actions";

function* sessionSaga() {
  yield fork(controllerSaga);
  yield fork(traceSaga);
  yield fork(evmSaga);
  yield fork(soliditySaga);
}

export default function *saga () {
  yield race({
    session: call(sessionSaga),

    cancel: take(END_OF_TRACE)
  });
}

import debugModule from "debug";
const debug = debugModule("debugger:session");
const reduxDebug = debugModule("debugger:redux");

import { createStore, applyMiddleware } from "redux";

import createSagaMiddleware from "redux-saga";
import createLogger from "redux-cli-logger";

import rootSaga from "./sagas";
import reducer from "./reducers";
import * as actions from "./controller/actions";

import trace from "./trace/selectors";
import evm from "./evm/selectors";

/**
 * Debugger Session
 */
export default class Session {
  /**
   * @param {function(state: State): StateView} viewer - function to view state
   * @param {State} initialState - initial state
   * @private
   */
  constructor(contexts, trace, initialState) {
    const sagaMiddleware = createSagaMiddleware();

    const loggerMiddleware = createLogger({
      log: reduxDebug,
      stateTransformer: (session) => session.state
    });

    this._store = createStore(
      reducer,

      {
        props: { contexts, trace },
        state: initialState
      },

      applyMiddleware(
        sagaMiddleware,
        loggerMiddleware
      )
    );

    sagaMiddleware.run(rootSaga);
  }

  get state() {
    return this._store.getState();
  }

  view(selector) {
    let { state, props } = this.state;

    return selector(state, props);
  }

  get finished() {
    return this.view(trace.step) === undefined;
  }

  get failed() {
    return this.finished && this.view(evm.current.callstack).length
  }

  dispatch(action) {
    if (this.finished) {
      debug("finished: intercepting action %o", action);

      return false;
    }

    this._store.dispatch(action);

    return true;
  }

  interrupt() {
    return this.dispatch(actions.interrupt());
  }

  advance() {
    return this.dispatch(actions.advance());
  }

  stepNext() {
    return this.dispatch(actions.stepNext());
  }

  stepOver() {
    return this.dispatch(actions.stepOver());
  }

  stepInto() {
    return this.dispatch(actions.stepInto());
  }

  stepOut() {
    return this.dispatch(actions.stepOut());
  }
}

export const JUMP = "JUMP";
export function jump(jumpDirection) {
  return {
    type: JUMP,
    jumpDirection
  };
}

import { combineReducers } from "redux";

import * as actions from "./actions";

export function functionDepth(state = 1, action) {
  if (action.type === actions.JUMP) {
    const delta = spelunk(action.jumpDirection)
    return state + delta;
  } else {
    return state;
  }
}

function spelunk(jump) {
  if (jump == "i") {
    return 1;
  } else if (jump == "o") {
    return -1;
  } else {
    return 0;
  }
}

const reducer = combineReducers({
  functionDepth
});

export default reducer;

import debugModule from "debug";
const debug = debugModule("debugger:solidity:sagas");

import { call, put, take } from "redux-saga/effects";
import { view } from "../effects";

import { TICK } from "../trace/actions";
import evm from "../evm/selectors";

import * as actions from "./actions";
import solidity from "./selectors";

function* functionDepthSaga () {
  while (true) {
    yield take(TICK);
    debug("got TICK");
    let instruction = yield view(solidity.nextStep.nextInstruction);
    debug("instruction: %o", instruction);

    if (yield view(evm.nextStep.isJump)) {
      let jumpDirection = yield view(solidity.nextStep.jumpDirection);


      yield put(actions.jump(jumpDirection));
    }
  }
}


export default function* saga () {
  yield call(functionDepthSaga);
}

import { createSelector, createStructuredSelector } from "reselect";

import context from "../context/selectors";
import evm from "../evm/selectors";

const functionDepth = (state, props) => state.solidity.functionDepth;

let currentState = createStructuredSelector({
  functionDepth
});
currentState.functionDepth = functionDepth;

const nextInstruction = createSelector(
  [context.current, evm.nextStep.programCounter],

  (context, pc) => context.instructionAtProgramCounter(pc)
);

const sourceRange = createSelector(
  [nextInstruction],

  (instruction) => {
    return {
      start: instruction.start,
      length: instruction.length,
      lines: instruction.range
    };
  }
);

const isMultiline = createSelector(
  [sourceRange],

  ( {lines} ) => lines.start.line != lines.end.line
);


const jumpDirection = createSelector(
  [nextInstruction],

  (instruction) => instruction.jump
);

let nextStep = createStructuredSelector({
  nextInstruction: nextInstruction,
  sourceRange: sourceRange,
  isMultiline: isMultiline,
  jumpDirection: jumpDirection,
});

nextStep.nextInstruction = nextInstruction;
nextStep.sourceRange = sourceRange;
nextStep.isMultiline = isMultiline;
nextStep.jumpDirection = jumpDirection;

let selector = createStructuredSelector({
  currentState,
  nextStep
});
selector.currentState = currentState;
selector.nextStep = nextStep;

export default selector;

