  return {type: END_OF_TRACE};
}

import { combineReducers } from "redux";

import { TOCK, END_OF_TRACE } from "./actions";

export function index(state = 0, action) {
  if (action.type == TOCK || action.type == END_OF_TRACE) {
    return state + 1;
  } else {
    return state;
  }
}

const reducer = combineReducers({
  index
});

export default reducer;

import debugModule from "debug";
const debug = debugModule("debugger:trace:sagas");

import { call, fork, take, takeLatest, put } from "redux-saga/effects";
import { view } from "../effects";

import * as actions from "./actions";
import trace from "./selectors";

export function* next() {
  let remaining = yield view(trace.stepsRemaining);
  debug("remaining: %o", remaining);
  let steps = yield view(trace.steps);
  debug("total steps: %o", steps.length);

  if (remaining > 0) {
    debug("putting TICK");
    // updates state for current step
    yield put(actions.tick());
    debug("put TICK");

    remaining--; // local update, just for convenience
  }

  if (remaining) {
    debug("putting TOCK");
    // updates step to next step in trace
    yield put(actions.tock());
    debug("put TOCK");

  } else {

    yield put(actions.endTrace());
  }
}

export default function* saga() {
  yield takeLatest(actions.NEXT, next);
}

import { createSelector, createStructuredSelector } from "reselect";

const traceStep = (state, props) => props.trace[state.trace.index];

const stepsRemaining = (state, props) =>
  props.trace.length - state.trace.index;

const steps = (state, props) => [...props.trace];

const index = (state, props) => state.trace.index;

let selector = createStructuredSelector({
  index,
  steps,
  stepsRemaining,
  step: traceStep,
});
selector.index = index;
selector.steps = steps;
selector.step = traceStep;
selector.stepsRemaining = stepsRemaining;

export default selector;

